package 滑动窗口;

import java.util.HashMap;
import java.util.Iterator;
import java.util.Map;

public class 最小覆盖字符串 {
    static Map<Character, Integer> ori = new HashMap<Character, Integer>();
    static Map<Character, Integer> cnt = new HashMap<Character, Integer>();


    /**
     * 输入：s = "ADOBECODEBANC", t = "ABC"
     * 输出："BANC"
     * @param s
     * @param t
     * @return
     */
    public static String minWindow(String s, String t) {
        String sub,minStr="";
        int min = Integer.MAX_VALUE,size = 0,j;
        if(t.length() > s.length()){
            return "";
        }
        //用ori存储t字符串每个字母对应的数量
        for(int i=0; i<t.length(); i++){
            ori.put(t.charAt(i),ori.getOrDefault(t.charAt(i),0) + 1);
        }
        for(int i=0; i < s.length(); i++) {
            //初始化
            size = 0;
            cnt.clear();
            j = i;
            while(j < s.length() && j - i + 1 < min){
                //判断字串是否包含t的所有字母
                //0：包含   1：不包含
                int flag = 0;
                //向cnt传入元素
                cnt.put(s.charAt(j),cnt.getOrDefault(s.charAt(j),0) + 1);
                //如果j和i的截距小于t的长度则跳过
                if(j - i + 1< t.length()){
                    j++;
                    continue;
                }

                sub = s.substring(i,j+1);
                //对t循环，看子串是否包含t的每个字母
                for(int k=0; k < t.length(); k++){
                    if(! cnt.containsKey(t.charAt(k))){
                        flag = 1;
                        break;
                    }
                }
                if(flag == 0){
                    if(min > (j - i + 1)){
                        if(check()) {
                            minStr = sub;
                            min = j - i + 1;
                            break;
                        }
                    }
                }
                j++;

            }
            if(j == s.length() && j - i + 1 < t.length()){
                break;
            }
        }
        return minStr;
    }
    public static boolean check() {
        Iterator iter = ori.entrySet().iterator();
        while (iter.hasNext()) {
            Map.Entry entry = (Map.Entry) iter.next();
            Character key = (Character) entry.getKey();
            Integer val = (Integer) entry.getValue();
            if (cnt.getOrDefault(key, 0) < val) {
                return false;
            }
        }
        return true;
    }


    public static void main(String[] args) {
        String s = "",
                t = "";
        String s1 = minWindow(s, t);
        最小覆盖字符串_标准答案 so = new 最小覆盖字符串_标准答案();
        String s2 = so.minWindow(s, t);
        System.out.println(s1.equals(s2));
    }
}
